'use strict';

module.exports = require('./lib/express');

var express = require('express');
var app = express();

//usando frases para star wars
var names = require('starwarsquotes');
var allNames = names.all;
var randomName = names.random();
var threeRandomNames = names.random(3);

//ruta predeterminada
app.get('/', function(req, res){
  res.send('Hola mundo');
});
//Creacion de ruta para x cosas.
app.get('/shit',function(req, res){
  res.send('Fuck that shit');
});
//ruta de frases de star wars
app.get('/swquotes',function(req,res){
  res.send(JSON.stringify(threeRandomNames));
});
//Aplicacion ejecutandose en el servidor puerto 3000.
app.listen(3000,function(){
  console.log('App ejecutandose en el puerto 3000');
});
